def Diophantine_representation_of_the_set_of_prime_numbers(ary)
  a, b, c, d, e, f, g, h, i, j, k, l, m, n, o, p, q, r, s, t, u, v, w, x, y, z = *ary
  f1 = w * z + h + j - q
  f2 = (g * k + 2 * g + k + 1) * (h + j) + h - z
  f3 = 2 * n + p + q + z - e
  f4 = 16 * (k + 1) ** 3 * (k + 2) * (n + 1) ** 2 + 1 - f ** 2
  f5 = e ** 3 * (e + 2) * (a + 1) ** 2 + 1 - o ** 2
  f6 = (a ** 2 - 1) * y ** 2 + 1 - x ** 2
  f7 = 16 * r ** 2 * y ** 4 * (a ** 2 - 1) + 1 - u ** 2
  f8 = ((a + u ** 2 * (u ** 2 - a)) ** 2 - 1) * (n + 4 * d * y) ** 2 + 1 - (x + c * u) ** 2
  f9 = n + l + v - y
  f10 = (a ** 2 - 1) * l ** 2 + 1 - m ** 2
  f11 = a * i + k + 1 - l - i
  f12 = p + l * (a - n - 1) + b* (2 * a* n + 2 * a - n ** 2 - 2 * n - 2) - m
  f13 = q + y * (a - p - 1) + s * (2 * a * p + 2 * a - p ** 2 - 2 * p - 2) -x
  f14 = z + p * l * (a - p) + t * (2 * a * p - p ** 2 - 1) - p * m
  puts [f1, f2, f3, f4, f5, f6, f7, f8, f9, f10, f11, f12, f13, f14]
  (k + 2) * (1 - [f1, f2, f3, f4, f5, f6, f7, f8, f9, f10, f11, f12, f13, f14].map{|i| i * i}.inject(:+))
end

